con:response.con:script:
import groovy.transform.Field

// create XmlHolder for request content
def holder = new com.eviware.soapui.support.XmlHolder( mockRequest.requestContent )
def fournisseur = "SNCF-ACCES"
// define namespace
holder.namespaces["siri"] = "http://www.siri.org.uk/siri"

// construction des valeurs communes de la response
requestContext.responseTimestamp=getCurrentTime()

// construction du header de la response
requestContext.responderRef=fournisseur
// rcupration de l'id message de la request
requestContext.requestMessageRefHeader=holder["//siri:MessageIdentifier"][0]

// construction du body de la response
// rcupration de l'id message de la request
requestContext.requestMessageRefBody=holder["//siri:MessageIdentifier"][1]
requestContext.subscriberRef=holder["//siri:SubscriberRef"]
requestContext.subscriptionRef=holder["//siri:SubscriptionIdentifier"]
requestContext.serviceStartedTime=getServiceStartedTime()

// Methodes utilitaires DATE
@Field Locale locale = Locale.FRENCH
@Field TimeZone timezone = TimeZone.getTimeZone("UTC")

// Methode Date permettant d'avoir la date courante  minuit
def getServiceStartedTime(){
	def calendar = Calendar.getInstance(timezone, locale);
	calendar.add(Calendar.HOUR, 0);
	calendar.set(Calendar.MINUTE, 0);
	calendar.set(Calendar.SECOND, 0);
	calendar.set(Calendar.MILLISECOND, 0);
	def calendarXML = javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(calendar);
	return calendarXML.normalize();
}

// Methode Date permettant d'avoir la date courante
def getCurrentTime(){
	def calendar = Calendar.getInstance(timezone, locale);
	def calendarXML = javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(calendar);
	return calendarXML.normalize();
}

// create XmlHolder for request content
def holder = new com.eviware.soapui.support.XmlHolder( mockRequest.requestContent )

// define namespace
holder.namespaces["siri"] = "http://www.siri.org.uk/siri"

// get arguments
requestContext.idMessage = holder["//siri:MessageIdentifier"]

def calendar = Calendar.getInstance();
calendar.add(Calendar.HOUR,-5);
requestContext.serviceStartedTime = javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(calendar);
