def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// create XmlHolder for request content
def holder = new com.eviware.soapui.support.XmlHolder( mockRequest.requestContent )
// define namespace
holder.namespaces["siri"] = "http://www.siri.org.uk/siri"
def actualTime = Calendar.getInstance()
// rcupration de l'id message de la request
def idMessage = holder["//siri:MessageIdentifier"][0]
def monitoringRef = holder["//siri:MonitoringRef"]
def version = holder["//Request/@version"].toString()
def ProducerID = "ACME"
def OperatorID = "OpACME"

//Liste des arrts
def StopPointIdList = ["4644","29066","12817","19785","5604","5603","5632","5197","5601"]
def StopPointNameList = ["G. Versailles Chantiers","Rive Droite","Centre Commercial Vlizy 2","Wagner","Gare de Sartrouville","Gare d'Argenteuil","Rue de Chatou","Gare de Montigny Beauchamp","Prfecture Gare"]

//Identification de l'arrt requt
def ind=0
for (int i = 0; i < 9; i++) {
	if ( monitoringRef.split(/:/)[3] == StopPointIdList[i] ) {
		ind = i
	}
}	

//introduction d'un retard d'une seconde toutes les 12 secondes (=retard 1mn toutes les 5mn)
Delay = actualTime.get(Calendar.MINUTE)*12 + (int) (actualTime.get(Calendar.SECOND)/5)

//Heure de passage de la premire course  desservir l'arrt
StartHour = 0
if (actualTime > getCalendarShiftSeconde(getCalendarMinuteSeconde(getCalendarShiftHour(actualTime, StartHour), 5*ind,2), Delay)) {
	StartHour = 1
}

def projectDir = groovyUtils.projectPath
def ListPassages = ""
def Passage = ""

def NbrPassages = mockResponse.mockOperation.mockService.getPropertyValue("NbrPassages").toInteger()
for (int i = 0; i < NbrPassages; i++) {
	Passage = new File(projectDir,"SM_MonitoredStopVisit.xml").text
	Passage = Passage.replaceAll('producerRef', ProducerID)
	Passage = Passage.replaceAll('monitoringRef', monitoringRef)
	Passage = Passage.replaceAll('ResponseMessageIdentifier', ProducerID+":ResponseMessage::"+String.valueOf((int)Math.random()*1000000000)+":LOC")
	Passage = Passage.replaceAll('operatorRef', ProducerID+":Operator::"+OperatorID+":LOC")
	Passage = Passage.replaceAll('recordedAtTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(actualTime).toString())
	Passage = Passage.replaceAll('lineRef', "STIF:Line::C00272:")
	Passage = Passage.replaceAll('dataFrameRef', "any")
	Passage = Passage.replaceAll('journeyPatternRef', OperatorID+":JourneyPattern::1:LOC")
	Passage = Passage.replaceAll('journeyPatternName', "mission")
	Passage = Passage.replaceAll('publishedLineName', "12")
	Passage = Passage.replaceAll('routeRef', OperatorID+":Route::1:LOC")
	Passage = Passage.replaceAll('directionName', "Prfecture Gare")
	Passage = Passage.replaceAll('originRef', "STIF:StopPoint:Q:4644:")
	Passage = Passage.replaceAll('originName', "G. Versailles Chantiers")
	Passage = Passage.replaceAll('placeRef', "STIF:StopPoint:Q:5604:")
	Passage = Passage.replaceAll('placeName', "Gare de Sartrouville")
	Passage = Passage.replaceAll('destinationRef', "STIF:StopPoint:Q:23358:")
	Passage = Passage.replaceAll('destinationName', "Prfecture Gare")
	Passage = Passage.replaceAll('StopPointRef', "STIF:StopPoint:Q:"+StopPointIdList[ind]+":")
	Passage = Passage.replaceAll('StopPointName', StopPointNameList[ind])

	//Dfinition des courses
	def cal = getCalendarShiftHour(actualTime,StartHour+i)
	VehiculeJourneyName = "Course"+Integer.toString(cal.get(cal.HOUR_OF_DAY))
	Passage = Passage.replaceAll('vehicleJourneyName', VehiculeJourneyName)
	Passage = Passage.replaceAll('itemIdentifier', OperatorID+":Item::"+StopPointIdList[ind]+"-"+VehiculeJourneyName+":LOC")
	cal = getCalendarMinuteSeconde(getCalendarShiftHour(actualTime, StartHour+i),0,0)
	Passage = Passage.replaceAll('originAimedDepartureTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
	cal = getCalendarMinuteSeconde(getCalendarShiftHour(actualTime, StartHour+i),40,0)
	Passage = Passage.replaceAll('destinationAimedArrivalTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())

	// Dfinition des passages
	Passage = Passage.replaceAll('Order', (i+1).toString())
	cal = getCalendarMinuteSeconde(getCalendarShiftHour(actualTime, StartHour+i), 5*ind,0)
	Passage = Passage.replaceAll('aimedArrivalTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
	Passage = Passage.replaceAll('aimedDepartureTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
	cal = getCalendarMinuteSeconde(getCalendarShiftHour(actualTime, StartHour+i), 5*ind,1)
	Passage = Passage.replaceAll('actualArrivalTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
	Passage = Passage.replaceAll('actualDepartureTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
	if (i==0 && StartHour==0) {
		//Retard sur le premier passage
		cal = getCalendarShiftSeconde(getCalendarMinuteSeconde(getCalendarShiftHour(actualTime, StartHour+i), 5*ind,2),Delay)
	} else {
		cal = getCalendarMinuteSeconde(getCalendarShiftHour(actualTime, StartHour+i), 5*ind,2)
	}
	Passage = Passage.replaceAll('expectedArrivalTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
	Passage = Passage.replaceAll('expectedDepartureTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
	Passage = Passage.replaceAll('Delay', "PT"+((int)Delay/60).toString()+"M")

	Passage = Passage.replaceAll('itemIdentifier', OperatorID+":Item::"+StopPointIdList[ind]+"-"+VehiculeJourneyName+":LOC")
	Passage = Passage.replaceAll('datedVehicleJourneyRef', OperatorID+":VehicleJourney::"+VehiculeJourneyName+":LOC")

	ListPassages = ListPassages + Passage
}

SMDelivery = new File(projectDir, "SM_Delivery.xml").text
SMDelivery = SMDelivery.replaceAll('ListMonitoredStopVisit', ListPassages)
SMDelivery = SMDelivery.replaceAll('monitoringRef', monitoringRef)
