			Passage = Passage.replaceAll('actualArrivalTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
			Passage = Passage.replaceAll('actualDepartureTime', javax.xml.datatype.DatatypeFactory.newInstance().newXMLGregorianCalendar(cal).toXMLFormat())
			if (i==0 && StartHour==0) {
