// Methodes utilitaires DATE

// Dcalage des secondes
def getCalendarShiftSeconde(Calendar cal_in, int seconde){
	def cal_out = cal_in.clone()
	cal_out.add(Calendar.SECOND,seconde)
	return cal_out
}

// Dcalage des minutes
def getCalendarShiftMinute(Calendar cal_in, int minute){
	def cal_out = cal_in.clone()
	cal_out.add(Calendar.MINUTE,minute)
	return cal_out
}

// Dcalage de l'heure
def getCalendarShiftHour(Calendar cal_in, int heure){
	def cal_out = cal_in.clone()
	cal_out.add(Calendar.HOUR,heure)
	return cal_out
}

// Methode permettant de setter les minutes et secondes sur l'heure en cours
def getCalendarMinuteSeconde(Calendar cal_in, int minute, int seconde){ 
	def cal_out = cal_in.clone()
	cal_out.set(Calendar.MINUTE, minute)
	cal_out.set(Calendar.SECOND, seconde)
	return cal_out
}

