				} else if (EAT != "") {
					displayTime = "arr=" + EAT
				} else if (AAT != "") {
					displayTime = "arr~" + AAT
				}
				log.info( "  " + NbrPassages + "   " + LineRef + "   " + displayTime + "   " + DestName)
